import { Component, OnInit } from '@angular/core';
import { UsersService } from '../users.service';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { User } from '../user';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-add-user',
  templateUrl: './add-user.component.html',
  styleUrls: ['./add-user.component.scss']
})
export class AddUserComponent  implements OnInit {
  roles:Array<any>=[];
  users:Array<User>=[];
  submited:boolean=false
   id:number=0

 addUserForm:FormGroup=new FormGroup({})


  constructor(private userService:UsersService, private fb:FormBuilder, private router:Router, private _activatedRoute:ActivatedRoute){
    this.id=Number(this._activatedRoute.snapshot.paramMap.get('id'))
    console.log(this.id)
  }



  ngOnInit(): void {
       this.roles= this.userService.getRoles(),
       this.users =this.userService.getUsers()

        this.addUserForm = this.fb.group({
          name:new FormControl( "",[Validators.required , Validators.minLength(3), Validators.maxLength(100)] ) ,
          email:new FormControl ("", [Validators.required, Validators.email]),
          role:new FormControl("",[Validators.required])
        }) 


        if(this.id >0){
             const data:any = this.users.find((userid)=>userid.id ===this.id)
            this.addUserForm.patchValue(data)
        }
  }


  get validation(){
    return this.addUserForm.controls
  }



  onSubmit(){
    if(this.addUserForm.invalid){
      this.submited=true;
      return
    }
    else{
      console.log({...this.addUserForm.value, id:this.users.length+1})
    this.userService.addUser({...this.addUserForm.value, id:this.users.length+1})
    Swal.fire({
      position: "top-end",
      icon: "success",
      title: "Your work has been saved",
      showConfirmButton: false,
      timer: 1500
    });
    this.router.navigate(['/userlist'])
    }
  }


  onUpdate(id:number){

  const indexValue=  this.users.findIndex((userData)=>userData.id ===id)


  console.log(indexValue ,this.addUserForm.value )
 // this.users[indexValue] ={...this.addUserForm.value, id:this.id}
  this.router.navigate(['/userlist'])
  }

}
